editor_template_static_head.script:
		
			function el_SetAlsoReadToEditorTemplate()
			{
				var LinkValue = document.getElementById("txt_LinkValue").value;
				var LinkTitle = document.getElementById("txt_LinkTitle").value;
				var LinkHref = document.getElementById("txt_LinkHref").value;
				var LinkRel = document.getElementById("txt_LinkRel").value;
				var LinkProtocol = document.getElementById("ddlst_LinkProtocol").value;
				var LinkTarget = document.getElementById("ddlst_LinkTarget").value;
				var AlsoReadLanguage = document.getElementById("hdn_AlsoReadLanguage").value;
				var UseBox = document.getElementById("cbx_UseBox").checked;
				
				var LinkTemplate = "<a rel='" + LinkRel + "' href='" + LinkProtocol + LinkHref + "' title='" + LinkTitle + "' target='" + LinkTarget + "'>" + LinkValue + "</a>"
				var BoxTemplate = "<fieldset><legend>" + AlsoReadLanguage + "</legend>" + LinkTemplate + "</fieldset>"

				ElanatVariant.EditorTemplateValue = (UseBox) ? BoxTemplate : LinkTemplate;
			}
